import { useState } from "react";
function Square({value, onClick}) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

export default function Board() {
  const [squares, setSquares] = useState(Array(3).fill(null).map(() => Array(3).fill(null)));
  const [xIsNext, setXIsNext] = useState(true);
  function handleClick(i, j) {
    if(squares[i][j] || calculateWinner(squares)) {
      return;
    }
    const newSquares = Array(3).fill(null).map((_, i) => squares[i].slice())
    newSquares[i][j] = xIsNext ? 'X' : 'O';
    setXIsNext(!xIsNext);
    setSquares(newSquares);
  }
  let winner = calculateWinner(squares);
  let status = xIsNext ? "Next Player: X" : "Next Player:O";
  if(winner) 
  return (
    <div>
      {squares.map((line, i) => (
        <div className="board-row">
          {line.map( (value, j) => 
            <Square value={value} onClick={() => handleClick(i, j)}/>
          )}
        </div>
      ))}
    </div>
  );
} 

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
