function getZeroByTenthOf(num) {
  return Math.random() * 10 <= num ? 0 : 1; // Generating 0 with probability of num/10
}

function createMaze(m, n) {
  let maze = [];
  for (let i = 0; i < m; i++) {
    let row = [];
    for (let j = 0; j < n; j++) {
      row.push(getZeroByTenthOf(8));
    }
    maze.push(row);
  }
  return maze;
}

function Square({num}) {

  function handleClick() {

  }
  return <button className={num===0 ? "square white" : "square black"}></button>
}

export default function Board() {
  let maze = createMaze(20, 20);
  let m = maze.length, n = maze[0].length;
  maze[0][0] = 0;
  maze[m-1][n-1] = 0;
  return (
    <div>
      {maze.map((line, index) => (
        <div className="board-row">
          {line.map((num) => (
            <Square num={num} />
          ))}
        </div>
      ))}
    </div>
  );
}
